
#' Plot the EIR *vs.* time
#'
#' @param model a [list] *e.g.*,  generated by `exDE::xde_setup()` that defines a model in `exDE`
#' @param clrs a [list] of colors for plotting
#' @param llty an [integer] (or integers) to set the `lty` for plotting
#' @param stable a [logical] set to FALSE for `orbits` and TRUE for `stable_orbits`
#' @param add a [logical] to plot axes only if FALSE
#'
#' @export
plot_EIR <- function(model, clrs="black", llty=1, stable=FALSE, add=FALSE){
  vars=with(model$outputs,if(stable==TRUE){stable_orbits}else{orbits})

  if(add==FALSE)
  with(vars$terms,{
    plot(time, eir, type = "n", xlab = "Time", ylab = "dEIR", ylim = range(0, eir))
    tk4 = pretty(365*eir)
    clr = "darkblue"
    axis(4, tk4, tk4, col=clr, col.axis =clr)
    mtext("aEIR", 4, -1.5, col = clr)
  })
  lines_EIR(vars$terms, model, clrs, llty)
}

#' Add lines for the EIR *vs.* time
#'
#' @param terms a [list] with the outputs of `exDE::parse_deout()`
#' @param model a [list] *e.g.*,  generated by `exDE::xde_setup()` that defines a model in `exDE`
#' @param clrs a [list] of colors for plotting
#' @param llty an [integer] (or integers) to set the `lty` for plotting
#'
#' @export
lines_EIR <- function(terms, model, clrs="black", llty=1){
  with(terms,{
    if(model$nStrata==1)
      lines(time, eir, col=clrs, lty = llty)
    if(model$nStrata>1){
      if(length(clrs)==1) clrs=rep(clrs, model$nStrata)
      if(length(llty)==1) llty=rep(llty, model$nStrata)
      for(i in 1:model$nStrata)
        lines(time, eir[,i], col=clrs[i], lty = llty)
    }
  })
}

